CustomField.formula:
IF(
  CONTAINS(Tiempo_primera_respuesta__c, "días"),
  /* Formato "X días Y horas" */
  VALUE(LEFT(Tiempo_primera_respuesta__c, FIND(" días", Tiempo_primera_respuesta__c) - 1)) * 24 * 60
  +
  VALUE(
    MID(
      Tiempo_primera_respuesta__c,
      FIND(" días", Tiempo_primera_respuesta__c) + LEN(" días "),
      FIND(" horas", Tiempo_primera_respuesta__c) - (FIND(" días", Tiempo_primera_respuesta__c) + LEN(" días "))
    )
  ) * 60,

  /* Formato "X horas Y minutos" */
  VALUE(LEFT(Tiempo_primera_respuesta__c, FIND(" horas", Tiempo_primera_respuesta__c) - 1)) * 60
  +
  VALUE(
    MID(
      Tiempo_primera_respuesta__c,
      FIND(" horas", Tiempo_primera_respuesta__c) + LEN(" horas "),
      FIND(" minutos", Tiempo_primera_respuesta__c) - (FIND(" horas", Tiempo_primera_respuesta__c) + LEN(" horas "))
    )
  )
)
